import React, { useEffect, useState, useRef } from 'react';
import '../styles/components.css';

function FeatureImportanceViewer({ featureImportanceData, topFeatures }) {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [circlePositions, setCirclePositions] = useState([]);
  const containerRef = useRef(null);
  
  // Feature positions are based on your specific plot - adjust as needed
  // These represent the top 3 features' positions as [x, y, radius] in the plot
  const TOP_FEATURE_POSITIONS = [
    [0.85, 0.18, 30],  // Top feature position (x%, y%, radius)
    [0.85, 0.3, 25],   // Second feature
    [0.82, 0.42, 20],  // Third feature
  ];

  useEffect(() => {
    if (imageLoaded && containerRef.current) {
      const container = containerRef.current;
      const rect = container.getBoundingClientRect();
      
      // Calculate positions based on container size
      const positions = TOP_FEATURE_POSITIONS.map(([x, y, r]) => ({
        x: x * rect.width,
        y: y * rect.height,
        r: (r / 100) * Math.min(rect.width, rect.height)
      }));
      
      setCirclePositions(positions);
    }
  }, [imageLoaded]);

  if (!featureImportanceData) return null;
  
  return (
    <div className="feature-importance-container">
      <h4>Feature Importance Analysis</h4>
      
      <div className="image-container" ref={containerRef} style={{ position: 'relative' }}>
        {/* The feature importance plot from the backend */}
        <img 
          src={featureImportanceData}
          alt="Feature Importance" 
          className="feature-importance-img"
          onLoad={() => setImageLoaded(true)}
        />
        
        {/* SVG overlay with circles */}
        {imageLoaded && (
          <svg 
            style={{ 
              position: 'absolute', 
              top: 0, 
              left: 0, 
              width: '100%', 
              height: '100%', 
              pointerEvents: 'none' 
            }}
          >
            {circlePositions.map((circle, index) => (
              <circle
                key={index}
                cx={circle.x}
                cy={circle.y}
                r={circle.r}
                fill="none"
                stroke={index === 0 ? "#ff5722" : "#ff9800"}
                strokeWidth={index === 0 ? 3 : 2}
                opacity={0.8 - (index * 0.2)}
              />
            ))}
          </svg>
        )}
      </div>
      
      <div className="top-features-list">
        <h5>Top Features (F-score ranking):</h5>
        <ol>
          {topFeatures?.slice(0, 5).map((feature, index) => (
            <li key={index} className={index === 0 ? "most-important" : ""}>
              {formatFeatureName(feature)}
              {index === 0 && <span className="top-badge">HIGHEST IMPORTANCE</span>}
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

function formatFeatureName(name) {
  if (!name) return '';
  return name
    .split('_')
    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
    .join(' ');
}

export default FeatureImportanceViewer;
