import React, { useState } from 'react';
import '../styles/components.css';
const BrainRegionsImage = ({ region }) => {
    return (
      <svg width="240" height="200" viewBox="0 0 240 200">
        {/* Frontal lobe outline */}
        <path 
          d="M120,30 C70,30 40,60 40,100 C40,140 70,160 120,160 C170,160 200,140 200,100 C200,60 170,30 120,30 Z" 
          fill="#f0f0f0" 
          stroke="#999" 
          strokeWidth="2"
        />
        
        {/* Source positions - small red circles */}
        <circle cx="70" cy="60" r="4" fill="red" /> <text x="65" y="50" fontSize="9">S1</text>
        <circle cx="90" cy="55" r="4" fill="red" /> <text x="85" y="45" fontSize="9">S2</text>
        <circle cx="110" cy="50" r="4" fill="red" /> <text x="105" y="40" fontSize="9">S3</text>
        <circle cx="130" cy="50" r="4" fill="red" /> <text x="125" y="40" fontSize="9">S4</text>
        <circle cx="150" cy="55" r="4" fill="red" /> <text x="145" y="45" fontSize="9">S5</text>
        <circle cx="170" cy="60" r="4" fill="red" /> <text x="165" y="50" fontSize="9">S6</text>
        <circle cx="180" cy="70" r="4" fill="red" /> <text x="175" y="60" fontSize="9">S7</text>
        <circle cx="185" cy="85" r="4" fill="red" /> <text x="180" y="75" fontSize="9">S8</text>
        
        {/* Detector positions - small blue circles */}
        <circle cx="80" cy="70" r="4" fill="blue" /> <text x="75" y="80" fontSize="9">D1</text>
        <circle cx="100" cy="65" r="4" fill="blue" /> <text x="95" y="75" fontSize="9">D2</text>
        <circle cx="120" cy="65" r="4" fill="blue" /> <text x="115" y="75" fontSize="9">D3</text>
        <circle cx="140" cy="65" r="4" fill="blue" /> <text x="135" y="75" fontSize="9">D4</text>
        <circle cx="160" cy="70" r="4" fill="blue" /> <text x="155" y="80" fontSize="9">D5</text>
        <circle cx="175" cy="80" r="4" fill="blue" /> <text x="170" y="90" fontSize="9">D6</text>
        <circle cx="180" cy="95" r="4" fill="blue" /> <text x="175" y="105" fontSize="9">D7</text>
        
        {/* Region highlights based on selection */}
        {region === 'prefrontal' && (
          <circle cx="85" cy="60" r="25" fill="none" stroke="#ff5722" strokeWidth="2" opacity="0.7" />
        )}
        {region === 'central_frontal' && (
          <circle cx="120" cy="60" r="25" fill="none" stroke="#ff5722" strokeWidth="2" opacity="0.7" />
        )}
        {region === 'lateral_frontal' && (
          <circle cx="170" cy="75" r="25" fill="none" stroke="#ff5722" strokeWidth="2" opacity="0.7" />
        )}
        
        {/* Legend */}
        <circle cx="40" cy="170" r="4" fill="red" />
        <text x="50" y="173" fontSize="10">Source</text>
        <circle cx="100" cy="170" r="4" fill="blue" />
        <text x="110" y="173" fontSize="10">Detector</text>
      </svg>
    );
  };

function InterpretationViewer({ interpretationData }) {
  const [activeTab, setActiveTab] = useState('features');
  const [selectedFeature, setSelectedFeature] = useState(null);
  const [showHelpModal, setShowHelpModal] = useState(false);
  
  if (!interpretationData) {
    return null;
  }
  
  const { region_descriptions, feature_explanations, event_descriptions } = interpretationData;
  
  // Sort features by region for better organization
  const sortedFeatures = Object.keys(feature_explanations || {}).sort((a, b) => {
    const regionA = a.split('_')[0];
    const regionB = b.split('_')[0];
    return regionA.localeCompare(regionB);
  });
  
  return (
    <div className="interpretation-container">
      <h3 className="section-title">Results Interpretation</h3>
      
      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'features' ? 'active' : ''}`}
          onClick={() => setActiveTab('features')}
        >
          Feature Meanings
        </button>
        <button 
          className={`tab ${activeTab === 'events' ? 'active' : ''}`}
          onClick={() => setActiveTab('events')}
        >
          Event Labels
        </button>
        <button 
          className={`tab ${activeTab === 'regions' ? 'active' : ''}`}
          onClick={() => setActiveTab('regions')}
        >
          Brain Regions
        </button>
        <button 
          className={`tab ${activeTab === 'processing' ? 'active' : ''}`}
          onClick={() => setActiveTab('processing')}
        >
          Processing Info
        </button>
      </div>
      
      <div className="tab-content">
        {activeTab === 'features' && (
          <div className="features-panel">
            <div className="feature-list">
              <h4>Top Features 
                <button className="help-button" onClick={() => setShowHelpModal(true)}>
                  ?
                </button>
              </h4>
              <ul>
                {sortedFeatures.slice(0, 15).map(feature => (
                  <li 
                    key={feature}
                    className={selectedFeature === feature ? 'selected' : ''}
                    onClick={() => setSelectedFeature(feature)}
                  >
                    {formatFeatureName(feature)}
                  </li>
                ))}
              </ul>
            </div>
            
            {selectedFeature && (
              <div className="feature-details">
                <h4>{formatFeatureName(selectedFeature)}</h4>
                <div className="detail-card">
                  <div className="brain-image-container">
                    <BrainRegionsImage region={feature_explanations[selectedFeature].region} />
                    <div className={`highlight ${feature_explanations[selectedFeature].region}`}></div>
                  </div>
                  <div className="explanation">
                    <p><strong>Region:</strong> {capitalize(feature_explanations[selectedFeature].region)} lobe</p>
                    <p><strong>Function:</strong> {feature_explanations[selectedFeature].region_function}</p>
                    <p><strong>Measurement:</strong> {feature_explanations[selectedFeature].measure_description}</p>
                    <p><strong>Wavelength:</strong> {feature_explanations[selectedFeature].wavelength_meaning}</p>
                    <p><strong>Physiological meaning:</strong> Changes in blood oxygenation during cognitive/motor tasks</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
        
        {activeTab === 'events' && (
          <div className="events-panel">
            <h4>Event Descriptions</h4>
            <table className="event-table">
              <thead>
                <tr>
                  <th>Event Label</th>
                  <th>Description</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(event_descriptions || {}).map(([event, description]) => (
                  <tr key={event}>
                    <td>{event}</td>
                    <td>{description}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        
        {activeTab === 'regions' && (
          <div className="regions-panel">
            <h4>Brain Regions & Functions</h4>
            <table className="region-table">
              <thead>
                <tr>
                  <th>Region</th>
                  <th>Function</th>
                  <th>Examples in Experiment</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(region_descriptions || {}).map(([region, details]) => (
                  <tr key={region}>
                    <td>{capitalize(region)}</td>
                    <td>{details.function}</td>
                    <td>{details.examples}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
        
        {activeTab === 'processing' && (
          <div className="processing-panel">
            <h4>Data Processing Information</h4>
            <div className="accordion">
              <div className="accordion-item">
                <div className="accordion-header">
                  ✅ Preprocessing Steps Applied
                </div>
                <div className="accordion-content">
                  <ul>
                    <li>Bandpass filtering (0.01-0.5 Hz)</li>
                    <li>Channel selection</li>
                    <li>Feature extraction</li>
                    <li>Feature selection using F-score</li>
                  </ul>
                </div>
              </div>
              <div className="accordion-item">
                <div className="accordion-header">
                  ⚠️ Processing Limitations
                </div>
                <div className="accordion-content">
                  <ul>
                    <li>No motion artifact correction was applied</li>
                    <li>Limited spatial resolution</li>
                    <li>Cross-validation with limited sample size</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      
      {showHelpModal && (
        <div className="modal-overlay" onClick={() => setShowHelpModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>How to Read Feature Names</h3>
              <button className="close-button" onClick={() => setShowHelpModal(false)}>×</button>
            </div>
            <div className="modal-body">
              <p>Feature names have the following structure:</p>
              <code>region_wavelength_measure</code>
              <ul>
                <li><strong>region:</strong> Brain area (frontal, temporal, parietal)</li>
                <li><strong>wavelength:</strong> Light wavelength used (760nm or 850nm)</li>
                <li><strong>measure:</strong> Type of measurement extracted from the signal</li>
              </ul>
              <p>Example: <code>frontal_850_early_mean</code> = Average signal in frontal region during early response phase, measuring oxygenated hemoglobin.</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

function formatFeatureName(name) {
  return name
    .split('_')
    .map(word => capitalize(word))
    .join(' ');
}

function capitalize(str) {
  return str.charAt(0).toUpperCase() + str.slice(1);
}

export default InterpretationViewer;
