import React, { useState, useEffect } from 'react';
import FileUploader from './components/FileUploader';
import FileList from './components/FileList';
import ActivitySelector from './components/ActivitySelector';
import PlotViewer from './components/PlotViewer';
import { fetchAvailableFiles, analyzeFile } from './api/apiService';
import './styles/App.css';
import InterpretationViewer from './components/InterpretationViewer';
import FeatureImportanceViewer from './components/FeatureImportanceViewer';
function App() {
  // State management
  const [files, setFiles] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [activities, setActivities] = useState({});
  const [selectedActivities, setSelectedActivities] = useState({});
  const [plots, setPlots] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  <img 
  ref={imageRef}
  src={featureImportanceData || '/assets/feature_importance.png'} // Fallback to local image
  alt="Feature Importance" 
  className="feature-importance-img"
  onLoad={() => setImageLoaded(true)}
/>
  // Load available files on component mount
  useEffect(() => {
    const loadFiles = async () => {
      try {
        const availableFiles = await fetchAvailableFiles();
        setFiles(availableFiles);
      } catch (err) {
        console.error('Error loading files:', err);
        setError('Failed to load available files');
      }
    };
    
    loadFiles();
  }, []);

  // Handle file selection
  const handleFileSelect = (fileId) => {
    const isSelected = selectedFiles.includes(fileId);
    
    if (isSelected) {
      setSelectedFiles(selectedFiles.filter(id => id !== fileId));
      setSelectedActivities(prev => {
        const updated = { ...prev };
        delete updated[fileId];
        return updated;
      });
    } else {
      setSelectedFiles([...selectedFiles, fileId]);
    }
  };

  // Handle successful file upload
  const handleFileUpload = (uploadedFile) => {
    setFiles(prev => [...prev, uploadedFile]);
  };

  // Handle activity selection
  const handleActivitySelect = (fileId, selectedActivitiesList) => {
    setSelectedActivities(prev => ({
      ...prev,
      [fileId]: selectedActivitiesList
    }));
  };

  // Handle analysis request
  const handleAnalyze = async () => {
    setLoading(true);
    setError(null);
    setPlots({});
    
    try {
      const results = {};
      
      // Process each selected file with its selected activities
      for (const fileId of selectedFiles) {
        if (selectedActivities[fileId] && selectedActivities[fileId].length > 0) {
          const fileResult = await analyzeFile(fileId, selectedActivities[fileId]);
          console.log("Analysis result received:", fileResult); // For debugging
          results[fileId] = fileResult;
        }
      }
      
      setPlots(results);
    } catch (err) {
      console.error('Analysis error:', err);
      setError('An error occurred during analysis');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>NIRS Analysis Dashboard</h1>
      </header>
      
      <main className="app-content">
        <section className="file-section">
          <h2>NIRS Files</h2>
          <FileUploader onFileUpload={handleFileUpload} />
          <FileList 
            files={files} 
            selectedFiles={selectedFiles} 
            onSelectFile={handleFileSelect}
          />
        </section>
        
        <section className="activity-section">
          <h2>Activities</h2>
          {selectedFiles.length > 0 ? (
            selectedFiles.map(fileId => (
              <ActivitySelector
                key={fileId}
                fileId={fileId}
                fileName={files.find(file => file.id === fileId)?.name || fileId}
                onSelectActivities={(activities) => handleActivitySelect(fileId, activities)}
              />
            ))
          ) : (
            <p className="info-text">Select files to view available activities</p>
          )}
          
          {selectedFiles.length > 0 && Object.keys(selectedActivities).length > 0 && (
            <button 
              className="analyze-button" 
              onClick={handleAnalyze}
              disabled={loading}
            >
              {loading ? 'Analyzing...' : 'Analyze Selected Data'}
            </button>
          )}
          
          {error && <p className="error-message">{error}</p>}
        </section>
        
        <section className="results-section">
          <h2>Analysis Results</h2>
          {Object.entries(plots).map(([fileId, plotData]) => (
            <div key={fileId} className="result-container">
              <PlotViewer 
                key={`plot-${fileId}`}
                fileName={files.find(file => file.id === fileId)?.name || fileId}
                plotData={plotData} 
              />
            {/* Add the FeatureImportanceViewer here */}
            {plotData.plots?.feature_importance && plotData.features?.top_features && (
              <FeatureImportanceViewer
                key={`feat-imp-${fileId}`}
                featureImportanceData={plotData.plots.feature_importance}
                topFeatures={plotData.features.top_features}
              />
            )}
            <InterpretationViewer 
              key={`interp-${fileId}`}
              interpretationData={plotData.interpretation || {
                region_descriptions: {
                  'prefrontal': {
                    'function': 'Executive functions and decision making',
                    'examples': 'Planning, decision making, response inhibition',
                    'anatomical_areas': 'Dorsolateral prefrontal cortex, orbitofrontal cortex'
                  },
                  'central_frontal': {
                    'function': 'Motor control and motor planning',
                    'examples': 'Movement planning, motor sequences',
                    'anatomical_areas': 'Supplementary motor area, premotor cortex'
                  },
                  'lateral_frontal': {
                    'function': 'Language processing and working memory',
                    'examples': 'Verbal tasks, short-term verbal memory',
                    'anatomical_areas': 'Broca\'s area, ventrolateral prefrontal cortex'
                  }
                },
                feature_explanations: plotData.features?.top_features?.length > 0 ? {
                  [plotData.features.top_features[0]]: {
                    'region': plotData.features.top_features[0].split('_')[0] || 'prefrontal',
                    'region_function': 'Executive functions and decision making',
                    'measure_description': 'Most important feature from machine learning analysis',
                    'wavelength_meaning': plotData.features.top_features[0].includes('850') ? 
                      '850nm - primarily sensitive to oxygenated hemoglobin' : 
                      '760nm - primarily sensitive to deoxygenated hemoglobin'
                  }
                } : {
                  'prefrontal_850_early_mean': {
                    'region': 'prefrontal',
                    'region_function': 'Executive functions and decision making',
                    'measure_description': 'Average activity in early response phase (1-4s after stimulus)',
                    'wavelength_meaning': '850nm - primarily sensitive to oxygenated hemoglobin'
                  }
                },
                event_descriptions: { 
                  'Exercise': 'Hand/finger movements', 
                  'Rest': 'Periods of inactivity',
                  'Imagination': 'Mental imagery of movement'
                }
              }}
            />
            </div>
          ))}
        </section>
      </main>
    </div>
  );
}

export default App;
